import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { AppService } from 'src/services/app.service';
import { BreadcrumbService } from 'src/services/breadcrumb.service';

@Component({
  selector: 'app-categories',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './categories.component.html',
  styleUrls: ['./categories.component.css']
})
export class CategoriesComponent implements OnInit {
  news: any[] = [];
  currentPosts: any[] = [];
  currentPage = 1;
  postsPerPage = 20; 
  pages: number[] = [];
  searchTerm: string = '';
  searchResults: any[] = [];

  constructor(private appService: AppService, private breadcrumbService: BreadcrumbService) {
    this.breadcrumbService.setBreadcrumbs(['Home', 'Search']);
  }

  ngOnInit(): void {
    this.appService.get((data: any) => {
      this.news = data.articles;
      this.setPageNumbers();
      this.setCurrentPage(1); 
    });
  }


  setCurrentPage(page: number) {
    this.currentPage = page;
    const lastPostIndex = this.currentPage * this.postsPerPage;
    const firstPostIndex = lastPostIndex - this.postsPerPage;
    this.currentPosts = this.news.slice(firstPostIndex, lastPostIndex);
  }

  setPageNumbers() {
    this.pages = Array(Math.ceil(this.news.length / this.postsPerPage))
      .fill(0)
      .map((x, i) => i + 1);
  }
  search(searchTerm: string) {
    const searchTermLowerCase = searchTerm.toLowerCase();
    this.searchResults = this.news.filter((post) => {
      return post.title.toLowerCase().includes(searchTermLowerCase) || post.description.toLowerCase().includes(searchTermLowerCase);
    });
  }
}
