import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { AppService } from 'src/app/services/app.service';

@Component({
  selector: 'app-entertainment',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './entertainment.component.html',
  styleUrls: ['./entertainment.component.css']
})
export class EntertainmentComponent {
  newsData: { [key: string]: any } = {};

  constructor(private appService: AppService) {}

  ngOnInit() {
    // Kategoriye göre haberleri çekmek için getNewsByCategory işlevini çağırın
    this.appService.getNewsByCategory('entertainment', (res) => {
      this.newsData = res; // Verileri saklayın
      console.log(this.newsData); // Verileri konsola yazdırın (isteğe bağlı)
    });
  }
}
