import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { NgxPaginationModule } from 'ngx-pagination';
import { AppService } from 'src/services/app.service';
import { BreadcrumbService } from 'src/services/breadcrumb.service';

@Component({
  selector: 'app-home',
  standalone: true,
  imports: [CommonModule, NgxPaginationModule],
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  news: any[] = [];
  currentPosts: any[] = [];
  currentPage = 1;
  postsPerPage = 6;
  pages: number[] = [];

  constructor(private appService: AppService, private breadcrumbService: BreadcrumbService) {
    this.breadcrumbService.setBreadcrumbs(['Home', '']);
  }

  ngOnInit(): void {
    this.appService.get((data: any) => {
      this.news = data.articles;
      this.setPageNumbers();
      this.setCurrentPage(1);
    });
  }

  setCurrentPage(page: number) {
    this.currentPage = page;
    const lastPostIndex = this.currentPage * this.postsPerPage;
    const firstPostIndex = lastPostIndex - this.postsPerPage;
    this.currentPosts = this.news.slice(firstPostIndex, lastPostIndex);
  }

  setPageNumbers() {
    this.pages = Array(Math.ceil(this.news.length / this.postsPerPage))
      .fill(0)
      .map((x, i) => i + 1);
  }

  // Sayfalama tıklaması için işlev
  onPageClick(pageNumber: number) {
    this.setCurrentPage(pageNumber);
  }
}
<!-- Slider Bileşeni -->
<div class="card mt-4">
  <div id="carouselExampleControlsNoTouching" class="carousel slide" data-bs-ride="carousel" data-bs-touch="false">
    <div class="carousel-inner" style="text-align: center;">
      <div class="carousel-item" *ngFor="let post of currentPosts; let i = index" [class.active]="i === 0">
        <a [href]="post.url" target="_blank">
          <img *ngIf="post.urlToImage" [src]="post.urlToImage" alt="Haber Resmi" style="max-height: 300px; margin: 0 auto; width: 100%;">
          <img *ngIf="!post.urlToImage" src="https://cdn.create.vista.com/api/media/small/47611223/stock-video-red-virtual-studio-background?videoStaticPreview=true&token=">
          <div class="carousel-caption" style="background: rgba(0, 0, 0, 0.5); color: white;">
            <h3 style="font-size: 18px;">{{ post.title }}</h3>
          </div>
        </a>
      </div>
    </div>
    <button class="carousel-control-prev" type="button" data-bs-target="#carouselExampleControlsNoTouching" data-bs-slide="prev">
      <span class="carousel-control-prev-icon" aria-hidden="true"></span>
      <span class="visually-hidden">Önceki</span>
    </button>
    <button class="carousel-control-next" type="button" data-bs-target="#carouselExampleControlsNoTouching" data-bs-slide="next">
      <span class="carousel-control-next-icon" aria-hidden="true"></span>
      <span class="visually-hidden">Sonraki</span>
    </button>
  </div>
</div>

<!-- Haberler Bileşeni -->
<div class="card mt-4">
  <div class="row row-cols-1 row-cols-md-2 g-4">
    <div class="col" *ngFor="let post of currentPosts">
      <div class="news-item card">
        <img *ngIf="post.urlToImage; else noImage" [src]="post.urlToImage" alt="Haber Resmi" class="card-img-top">
        <ng-template #noImage>
          <img src="yedek_resim_yolu" alt="Yedek Resim" class="card-img-top">
        </ng-template>
        <div class="card-body">
          <h5 class="card-title">{{ post.title }}</h5>
          <p class="card-text">{{ post.description }}</p>
          <a [href]="post.url" target="_blank" class="btn btn-primary">Devamını Oku</a>
        </div>
      </div>
    </div>
  </div>
</div>

<!-- Sayfalama Bileşeni -->
<ul class="pagination" style="float: right;">
  <li class="page-item" *ngFor="let page of pages; let i = index" [class.active]="i + 1 === currentPage">
    <a class="page-link" (click)="onPageClick(i + 1)">{{ i + 1 }}</a>
  </li>
</ul>
