import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { AppService } from 'src/services/app.service';
import { BreadcrumbService } from 'src/services/breadcrumb.service';


@Component({
  selector: 'app-business',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './business.component.html',
  styleUrls: ['./business.component.css'],
  providers: [
    {
      provide: 'breadcrumb',
      useValue: 'Business' 
    }
  ]
})
export class BusinessComponent implements OnInit {
  currentPosts: any[] = []; // currentPosts adlı değişkeni tanımlayın

  constructor(private appService: AppService, private breadcrumbService: BreadcrumbService) {
    this.breadcrumbService.setBreadcrumbs(['Home', 'Business']);
  }

  ngOnInit() {
    this.appService.getNewsByCategory('business', (res) => {
      this.currentPosts = res.articles; // Haberleri currentPosts'a aktarın
    });
  }



}
