import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { AppService } from 'src/services/app.service';

@Component({
  selector: 'app-sports',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './sports.component.html',
  styleUrls: ['./sports.component.css']
})
export class SportsComponent {
  newsData: { [key: string]: any } = {};


  constructor(private appService: AppService, private breadcrumbService: BreadcrumbService) {
    // Breadcrumb verilerini ayarla
    this.breadcrumbService.setBreadcrumbs(['Home', 'Business']);
  }

  ngOnInit() {
    // Kategoriye göre haberleri çekmek için getNewsByCategory işlevini çağırın
    this.appService.getNewsByCategory('sports', (res) => {
      this.newsData = res; // Verileri saklayın
      console.log(this.newsData); // Verileri konsola yazdırın (isteğe bağlı)
    });
  }
}
